$(function(){$("input").iCheck({checkboxClass:"icheckbox_flat-green",radioClass:"iradio_flat-green",increaseArea:"20%"});$(document).on("click","#save",function(){var n={};n.Id=$(this).attr("data-id");n.Name=$("#name").val();n.Address=$("#address").val();n.Age=$("#age").val();n.QQ=$("#qq").val();n.Wechat=$("#wechat").val();n.Gender=$('input:radio[name="gender"]:checked').val();n.Phone=$("#phone").val();n.Email=$("#email").val();$(this).attr("disabled","disabled");$.ajax({type:"POST",url:"/Instructor/User/EditProfile",data:n,dataType:"json",success:function(n){n.msg!==undefined&&(bootbox.dialog({message:n.msg}),n.success&&setTimeout(function(){window.location.href=window.location.href},2e3));$("#save").removeAttr("disabled")},error:function(n){console.log(n);$("#save").removeAttr("disabled")}})})});